import React, {Component} from "react";

class Timer extends Component {
    constructor(props) {
        super(props);

        this.countdown = props.countdown

        this.state = {time: 'please wait, time is calculated'};

        this.updateTime = this.updateTime.bind(this);

        setInterval(this.updateTime, 500);
    }

    componentDidMount() {
        this.interval = setInterval(this.updateTime, 500);
    }

    componentWillUnmount() {
        clearInterval(this.interval);
    }

    updateTime() {
        var datum = new Date();
        var Uhrzeit = datum.toTimeString();

        this.setState({time: Uhrzeit});
    }

    render() {
        return(<>
            <h3>Countdown von {this.countdown}</h3>
            {this.state.time}
            <div>
                <button onClick={this.click}>Start</button>
            </div>
        </>)
    }


}

export default Timer;
